svg.script:
(function() {
  // Manejo de clics en nodos
  const nodes = document.querySelectorAll('.node');
  nodes.forEach(node => {
    node.addEventListener('click', function() {
      const nodeType = this.getAttribute('data-node');
      console.log(`Nodo activado: ${nodeType}`);

      // Efecto visual de activación
      this.style.filter = 'url(#glow-strong)';
      setTimeout(() => {
        this.style.filter = '';
      }, 1500);
    });

    node.addEventListener('mouseenter', function() {
      this.style.transform = 'scale(1.05)';
    });

    node.addEventListener('mouseleave', function() {
      this.style.transform = 'scale(1)';
    });
  });

  // Pausar/reanudar animaciones al hacer clic
  let animationsPaused = false;
  document.addEventListener('keydown', function(e) {
    if (e.code === 'Space') {
      e.preventDefault();
      const allAnimated = document.querySelectorAll('[class*="flow-"], .species-node, .parliament-circle, .acoustic-wave');

      if (animationsPaused) {
        allAnimated.forEach(el => el.style.animationPlayState = 'running');
        animationsPaused = false;
      } else {
        allAnimated.forEach(el => el.style.animationPlayState = 'paused');
        animationsPaused = true;
      }
    }
  });

  console.log('Framework Bio-Token V2.0 cargado. Presiona ESPACIO para pausar/reanudar animaciones.');
})();
